import { IngredientType } from "../../data/cocktails";

type IngredientCheckboxProps = {
  ingredient: IngredientType;
  onIngredientChange: (ingredient: string) => void;
  selectedIngredients: string[];
}

const IngredientCheckbox = ({ ingredient, onIngredientChange, selectedIngredients }: IngredientCheckboxProps) => {
  return (
    <div className="block">
      <label>
        <input
          type="checkbox"
          checked={selectedIngredients.includes(ingredient.ingredient)}
          onClick={() => onIngredientChange(ingredient.ingredient)} />
        {ingredient.ingredient}
      </label>
    </div>
  );
}

export default IngredientCheckbox;
