import { useCallback, useState } from "react";

type DropdownProps = {
  buttonName: string;
  onChange: Function;
  options: {
    name: string;
    value: string;
  }[];
  selectedOptions: string[];
}

const MultiSelect = ({ buttonName, onChange, options, selectedOptions }: DropdownProps) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const renderOption = useCallback((option: { name: string; value: string; }, index: number) => {
    const checked = selectedOptions.includes(option.value);
    return (
      <p key={index} onClick={() => onChange(option.value)}>
        <input type="checkbox" checked={checked} onChange={() => { }} />
        {option.name}
      </p>
    )
  }, [selectedOptions]);

  const dismissHandler = (event: React.FocusEvent<HTMLDivElement>): void => {
    if (event.currentTarget === event.target) {
      setShowDropdown(false);
    }
  };

  return (
    <div className="inline" onBlur={dismissHandler}>
      <button
        className={showDropdown ? "active" : undefined}
        onClick={toggleDropdown}
      >
        {buttonName}
      </button>
      {showDropdown && (
        <>
          <div className={showDropdown ? "dropdown" : "dropdown active"}>
            {options.map((option, index) => {
              return renderOption(option, index);
            })}
          </div>
        </>
      )}
    </div>
  )
}

export default MultiSelect;
