import MultiSelect from "./helperComponents/MultiSelect";
import { useFindCocktailsContext } from "../context/FindCocktailsContext";
import { cocktailsData } from "../../data/cocktails";

const spiritOptions = [
  { name: 'Gin', value: 'gin' },
  { name: 'Rum', value: 'rum' },
  { name: 'Tequila', value: 'tequila' },
  { name: 'Vodka', value: 'vodka' },
  { name: 'Whiskey', value: 'whiskey' },
];

const Body = () => {
  const { selectedSpirits, setSelectedSpirits } = useFindCocktailsContext();

  const onSpiritsChange = (selectedSpirit: string) => {
    if (selectedSpirits.includes(selectedSpirit)) {
      setSelectedSpirits(selectedSpirits.filter(spirit => spirit !== selectedSpirit));
    } else {
      setSelectedSpirits([...selectedSpirits, selectedSpirit]);
    }
  };

  const filteredCocktails = cocktailsData.filter(cocktail => cocktail.spirits.filter(spirit => selectedSpirits.includes(spirit)).length);

  return (
    <div className="body">
      <div>
        <span className="m-left-20">Filter by</span>
        <span className="m-left-20">
          <MultiSelect
            buttonName="Spirits"
            onChange={onSpiritsChange}
            options={
              spiritOptions
            }
            selectedOptions={selectedSpirits} />
        </span>
      </div>
      <div>
        Filters: {selectedSpirits}
      </div>
      <div>
        {filteredCocktails.map(cocktail => {
          return (
            <div className="m-left-20" key={cocktail.name}>
              <p>{cocktail.label}</p>
              <ul>
                {cocktail.ingredients.map(ingredient => {
                  return <li>{ingredient.amount} {ingredient.ingredient}</li>
                })}
              </ul>
            </div>
          )
        })}
      </div>
    </div>
  );
}

export default Body;
