import { useState } from "react";

import MultiSelect from "./helperComponents/MultiSelect";

const Body = () => {
  const [selectedSpirits, setSelectedSpirits] = useState<string[]>([]);

  const onSpiritsChange = (selectedSpirit: string) => {
    if (selectedSpirits.includes(selectedSpirit)) {
      return selectedSpirits.filter(spirit => spirit !== selectedSpirit);
    } else {
      return selectedSpirits.push(selectedSpirit);
    }
  };

  return (
    <div className="body">
      <div>
        <span className="m-left-20">Filter by</span>
        <span className="m-left-20">
          <MultiSelect buttonName="Spirits" onChange={onSpiritsChange} options={
            [{ name: 'gin', value: 'gin' }, { name: 'vodka', value: 'vodka' }, { name: 'rum', value: 'rum' }]
          } selectedOptions={selectedSpirits} />
        </span>
      </div>
    </div>
  );
}

export default Body;
