import { useState } from "react";

type DropdownProps = {
  buttonName: string;
  id: string;
  onChange: Function;
  options: {
    name: string;
    value: string;
  }[];
}

const MultiSelect = ({ buttonName, id, onChange, options }: DropdownProps) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const dismissHandler = (event: React.FocusEvent<HTMLButtonElement>) => {
    if (event.currentTarget === event.target) {
      setShowDropdown(false);
    }
  }

  return (
    <button className={showDropdown ? "dropdown-active" : undefined}
      onClick={toggleDropdown}
      onBlur={(e: React.FocusEvent<HTMLButtonElement>) => dismissHandler(e)}
    >
      {buttonName}
    </button>
  )
}

export default MultiSelect;
