import { useCallback } from "react";

type DropdownProps = {
  buttonName: string;
  onChange: Function;
  onOpenChange: (showDropdown: boolean) => void;
  options: {
    name: string;
    value: string;
  }[];
  selectedOptions: string[];
  showDropdown: boolean;
  setShowDropdown: (showDropdown: boolean) => void;
}

const MultiSelect = ({ buttonName, onChange, onOpenChange, options, selectedOptions, showDropdown, setShowDropdown }: DropdownProps) => {
  const toggleDropdown = () => {
    if (!showDropdown) {
      onOpenChange(!showDropdown);
    }
  };

  const renderOption = useCallback((option: { name: string; value: string; }, index: number) => {
    const checked = selectedOptions.includes(option.value);
    return (
      <p key={index} onClick={() => onChange(option.value)}>
        <input type="checkbox" checked={checked} onChange={() => { }} />
        {option.name}
      </p>
    )
  }, [onChange, selectedOptions]);

  const dismissHandler = (event: React.FocusEvent<HTMLButtonElement>): void => {
    if (event.currentTarget === event.target) {
      setShowDropdown(false);
    }
  };

  return (
    <>
      <button
        className={showDropdown ? "active" : undefined}
        onClick={toggleDropdown}
        onBlur={dismissHandler}
      >
        {buttonName}
        {showDropdown && (
          <>
            <div className={showDropdown ? "dropdown" : "dropdown active"}>
              {options.map((option, index) => {
                return renderOption(option, index);
              })}
            </div>
          </>
        )}
      </button>
    </>
  )
}

export default MultiSelect;
