import { useState } from "react";

type DropdownProps = {
  buttonName: string;
  id: string;
  onChange: Function;
  options: {
    name: string;
    value: string;
  }[];
}

const MultiSelect = ({ buttonName, id, onChange, options }: DropdownProps) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const dismissHandler = (event: React.FocusEvent<HTMLButtonElement>) => {
    if (event.currentTarget === event.target) {
      setShowDropdown(false);
    }
  }

  return (
    <button
      className={showDropdown ? "active" : undefined}
      onClick={toggleDropdown}
      onBlur={(e: React.FocusEvent<HTMLButtonElement>) => dismissHandler(e)}
    >
      {buttonName}
      {showDropdown && (
        <>
          <div className={showDropdown ? "dropdown" : "dropdown active"}>
            {options.map((option, index) => {
              return (
                <p key={index} onClick={() => onChange(option.value)}>
                  {option.name}
                </p>
              )
            })}
          </div>
        </>
      )}
    </button>
  )
}

export default MultiSelect;
