export type SpiritType = 'gin' | 'rum' | 'tequila' | 'vodka' | 'whiskey';
export type FlavorType = 'bitter' | 'floral' | 'fruity' | 'sour' | 'sweet';
export type IngredientType = {
  ingredient: string;
  amount: string;
};

export type CocktailType = {
  name: string;
  label: string;
  spirits: SpiritType[];
  flavors: FlavorType[];
  ingredients: IngredientType[];
  directions: string[];
}

type CocktailsDataType = CocktailType[];

export const cocktailsData: CocktailsDataType = [
  {
    name: 'beesknees',
    label: "Bee's Knees",
    flavors: ['floral', 'sour'],
    spirits: ['gin'],
    ingredients: [
      {
        ingredient: 'gin',
        amount: '2 ounces'
      },
      {
        ingredient: 'lemon juice',
        amount: '3/4 ounces'
      },
      {
        ingredient: 'honey syrup',
        amount: '1/2 ounces'
      }
    ],
    directions: ['Add the gin, lemon juice, and honey syrup into a shaker with ice and shake until well-chilled.', 'Strain into a chilled cocktail glass.', 'Garnish with a lemon twist.']
  },
  {
    name: 'pinacolada',
    label: "Piña Colada",
    flavors: ['fruity', 'sweet'],
    spirits: ['rum'],
    ingredients: [
      {
        ingredient: 'rum',
        amount: '2 ounces'
      },
      {
        ingredient: 'cream of coconut',
        amount: '2 ounces'
      },
      {
        ingredient: 'pineapple juice',
        amount: '6 ounces'
      },
      {
        ingredient: 'ice',
        amount: '2 cups'
      }
    ],
    directions: ['Blend all ingredients in a blender and serve.']
  },
];
