type DropdownProps = {
  buttonName: string;
  id: string;
  onChange: Function;
  options: {
    name: string;
    value: string;
  }[];
}

const Dropdown = ({ buttonName, id, onChange, options }: DropdownProps) => {
  const onClick = () => {
    document.getElementById(`dropdown-${id}`)?.classList.toggle('show');
  }

  const onKeyUp = () => {
    var div, input, filter, a, i, textValue;
    input = document.getElementById(`dropdown-input-${id}`);
    filter = input!.getAttribute('value')!.toUpperCase();
    div = document.getElementById(`dropdown-${id}`);
    a = div!.getElementsByTagName("a");
    for (i = 0; i < a!.length; i++) {
      textValue = a[i].textContent || a[i].innerText;
      if (textValue.toUpperCase().indexOf(filter) > -1) {
        a[i].style.display = '';
      } else {
        a[i].style.display = 'none';
      }
    }
  }

  return (
    <div className="dropdown">
      <button className="dropdown-button" onClick={onClick}>
        {buttonName}
      </button>
      <div id={`dropdown-${id}`} className="dropdown-content">
        <input className="dropdown-input" type="text" placeholder="Search.." id={`dropdown-input-${id}`} onKeyUp={onKeyUp} />
        {options.map(option => {
          return <a onClick={onChange(option.value)}>{option.name}</a>
        })}
      </div>
    </div>
  )
}

export default Dropdown;
